examples.code:
// Method 1: Using global openModal function (assumes modal-helpers.js is loaded)
openModal('my-modal-id');
closeModal('my-modal-id');

// Method 2: Using Alpine.js events directly
window.dispatchEvent(new CustomEvent('open-modal-my-modal-id'));
window.dispatchEvent(new CustomEvent('close-modal-my-modal-id'));

// Method 3: Button with onclick
<button onclick="openModal('my-modal-id')">Open Modal</button>

// Method 4: Button with data attribute (auto-initialized)
<button data-modal-trigger="my-modal-id">Open Modal</button>

// Method 5: Load content via AJAX
loadModalContent('my-modal-id', '/api/content');

column_renderers.code:
'renderer' => function($row, $view) {
    $icon = FileIcon::fromFilename($row->name);
    $iconHtml = $view->element('atoms/icon', ['name' => $icon]);
    return '<div class="flex items-center gap-3">' . $iconHtml . h($row->name) . '</div>';
}
